import React, { useEffect, useRef } from "react";
import Globe from 'react-globe.gl';
import './App.css';

function Map (props) {
  const globeEl = useRef();
  useEffect(() => {
    const controls = globeEl.current.controls();
    controls.autoRotate=true;
    controls.autoRotateSpeed="1.0";
    controls.maxDistance="280.0";
    const render = globeEl.current.rendererConfig;
    render.enablePointerInteraction = false;
    globeEl.current.renderer.setSize( "100vw", "100vh" );
    /*const renderer = globeEl.current.renderer();
    renderer.setSize(5, 5, true);
    renderer.setViewport(1,1,1,1);
    document.body.appendChild( renderer.domElement );*/
  }, []);

 return (
    <>
     <div className="GlobeMap">
      <Globe
      ref={globeEl}
      globeImageUrl="//unpkg.com/three-globe/example/img/earth-night.jpg"
      />
      </div>
    </>
 );
}

export default Map
