function Map (props) {
  //const { useRef } = React;
  const globeEl = useRef();
  useEffect(() => {
    const controls = globeEl.current.controls();
    controls.autoRotate=true;
    controls.autoRotateSpeed="500.0"
  }, []);


 return (
   <div className="test">
     <Globe
     ref={globeEl}
     globeImageUrl="//unpkg.com/three-globe/example/img/earth-night.jpg"
     width="800"
     />
    </div>
 );
}





export default Map
