import React, {useEffect, useState} from "react";
import Apod from './apod.js';
import './App.css';

function ApodCollapsibleHeader () {
  const [apod, setApod] = useState([]);
  const [toggle, setToggle] = useState(false);
  const [url_type, setUrl] = useState([]);
  const [media_type, setMediaType] = useState("");

  useEffect(() => {
    getApod();
  }, []);

  const getApod = async () => {
    /* Get day in YYYY-MM-DD format for fetch*/
    const today = new Date();
    var dd = today.getDate();
    var mm = today.getMonth()+1;
    var yyyy = today.getFullYear();
    if(dd<10) {dd='0'+dd;};
    if(mm<10) { mm='0'+mm};
    var day = yyyy+'-'+mm+'-'+dd;
    console.log(day);
    const response = await fetch('https://api.nasa.gov/planetary/apod?'
      + new URLSearchParams({
        date: day,
        hd: true,
        api_key: 'WMtTrqwesceUk1odHg4uWnSjNH2FMdWVQdw0h6zG',
      }));
    const data = await response.json();
    setApod(data);
    console.log(data);
    console.log(data.media_type);
    setMediaType(data.media_type);
    console.log(media_type);
    console.log((String(media_type) === "video"));
    setUrl((media_type === "video")? data.url : data.hdurl);
    console.log(url_type);
  };

  const toggle_change = () => {
    setToggle(!toggle);
  };

  return (
    <div>
      <p onClick={e => toggle_change()} className="toggle_list">
        NASA Astronomy Picture of the Day
      </p>
      {toggle?(
        <div>
            <Apod
            name={apod.title}
            copyright={apod.copyright}
            explanation={apod.explanation}
            media_type={media_type}
            img_url={url_type}
            />
      </div>): null}
   </div>
  );
}

export default ApodCollapsibleHeader;
